msg.headers = {
    "Content-Type": "application/x-www-form-urlencoded",
    "Authorization": "ur key"
};
// 在原有内容的基础上加上 "測試用" 文字
msg.payload = "message=" + encodeURIComponent("*此為測試用訊息：" + msg.payload.content);
msg.method = "POST";
msg.url = "https://notify-api.line.me/api/notify";

return msg;
